let initialState = {
    candidates: [
        { name: 'Tareq', votes: 0 },
        { name: 'Suhaib', votes: 0 },
        { name: 'Ibrahim', votes: 0 },
    ],
    totalVotes: 0,
}

export default (state = initialState, action) => {
    let { type, payload } = action;
    switch (type) {
        case 'increment':
            let totalVotes = state.totalVotes + 1;
            let candidates = state.candidates.map(candidate => {
                if (candidate.name === payload) {
                    return { name: candidate.name, votes: candidate.votes + 1 }
                }
                return candidate;
            });
            return { totalVotes, candidates };

        case 'reset':
            return initialState;
        default:
            return state;
    }
}
// actions
export const increment = (name) => {
    return {
        type: 'increment',
        payload: name
    }
}

export const decrement = (name) => {
    return {
        type: 'decrement',
        payload: name
    }
}

export const reset = (name) => {
    return {
        type: 'reset',
        payload: name
    }
}
